INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (41, 'Sr Application Security Engineer at NetSuite (San Mateo, CA)', '&lt;p&gt;&lt;strong&gt;Summary:&lt;/strong&gt;&lt;strong&gt;&lt;br&gt; &lt;/strong&gt;As a senior security engineer you will focus on finding and fixing vulnerabilities, promoting good security practices, and designing security frameworks for use by NetSuite developers. The ideal candidate loves both building and breaking software. This is a job where details matter, and there are a lot of details, including all of web security, mobile security, cloud security and software security. You must be able to tell the difference between a big problem, a minor weakness, and a false positive. You must help developers understand security concepts and security practices. You need to be friendly but uncompromising when it comes to getting security right.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;br&gt; &lt;/strong&gt;&amp;bull; Identify, reproduce, and report security issues&lt;br&gt; &amp;bull; Do security reviews for feature design/implementations&lt;br&gt; &amp;bull; Design and implement Security APIs&lt;/p&gt;', 'San Mateo, CA', 'http://careers.stackoverflow.com/jobs/73532/sr-application-security-engineer-netsuite', 'Thu, 08 Oct 2015 23:02:01 Z', '2015-10-08T23:02:01Z', 41);
