INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (59, 'Security Researcher  at Fastly (San Francisco, CA)', '&lt;p&gt;&lt;strong&gt;SECURITY RESEARCHER&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Security Researcher at Fastly you will help ensure our security efforts are rooted in a deep understanding of the state of the art in web application vulnerabilities, denial of service attacks and bot mitigation techniques and methods.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for security researchers at all levels of seniority who enjoy investigating web application security threats to contribute intelligence to our security products and services. Qualified candidates will show an aptitude for discovering complex security issues that affect modern web sites. We encourage our security researchers to present at network and security conferences and participate in the security community.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is a role with a high impact, friendly security team. In addition to contributing to industry leading security products and services, you&amp;rsquo;ll make sure our customers benefit from a service built to the highest security standards in the industry. We&amp;rsquo;re in beautiful downtown San Francisco, but for the right candidate, we&amp;rsquo;re open to considering a remote position, and we have the team and tools in place to make it work.&lt;/p&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99993/security-researcher-fastly', 'Thu, 08 Oct 2015 22:02:01 Z', '2015-10-08T22:02:01Z', 59);
