INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (68, 'Application Developer, C# at Hyland Software (Westlake, OH)', '&lt;p&gt;&lt;strong&gt;Summary:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The&amp;nbsp;Application&amp;nbsp;Developer is responsible for developing, and maintaining intuitive business process and/or social web applications for internal and external clients.&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Position Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Increase business value through application development&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create exceptional &amp;amp; functional applications, and back-end databases using various programming and scripting languages&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Communicate development solutions to stakeholders in a precise and clear manner&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continue education in the latest C# .net development technologies through training courses and self-guided processes&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintain an ongoing and up-to-date knowledge of client-side scripting, and the more common JS Libraries&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintain an ongoing and up-to-date knowledge of Telligent and other platform API&amp;rsquo;s&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Document project code and technical processes through the standards set forth by the applications team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assist in training and support of end-users on developed applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Play lead role in project or systems implementation, as needed&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Westlake, OH', 'http://careers.stackoverflow.com/jobs/67675/application-developer-c-sharp-hyland-software', 'Thu, 08 Oct 2015 22:02:01 Z', '2015-10-08T22:02:01Z', 68);
