INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (76, 'Senior Backend Web Engineer for Animation Pipeline in the Cloud at Nimble Collective (Mountain View, CA)', '&lt;p&gt;&lt;span&gt;Nimble Collective seeks a prolific, passionate, driven senior developer to design and implement our backend service-based architecture. You will be instrumental in designing the services that control the flow of data in an animation pipeline, working closely with our pipeline and frontend engineers. Understanding of Computer Generated Imagery process is a bonus.&lt;/span&gt;&lt;/p&gt;', 'Mountain View, CA', 'http://careers.stackoverflow.com/jobs/99988/senior-backend-web-engineer-for-animation-nimble-collective', 'Thu, 08 Oct 2015 22:02:01 Z', '2015-10-08T22:02:01Z', 76);
