INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (92, 'Ruby on Rails Developer at Altamira (McLean, VA)', '&lt;p&gt;The selected candidate will provide software engineering support on a web-based administrative support portal.&amp;nbsp; The application consists of a suite of integrated tools that provides support personnel with key data points by reaching out to a variety of enterprise data sources and consolidating the data into a unified view.&amp;nbsp; Candidate will be responsible for maintaining the current set of tools which will allow for increased familiarity with the application.&amp;nbsp; Over time, the selected candidate will be responsible for developing new applications, utilizing enterprise web-services, and building new reports. &amp;nbsp;The successful candidate will be a member of a dynamic team that is responsible for a rich web application.&amp;nbsp; The selected candidate must have the ability to work in a mixed technical environment and maintain productivity when switching between multiple tools/applications and technologies.&amp;nbsp; The candidate must work well in a team environment and have the desire to develop software in an agile fashion.&lt;/p&gt;', 'McLean, VA', 'http://careers.stackoverflow.com/jobs/86167/ruby-on-rails-developer-altamira', 'Thu, 08 Oct 2015 22:02:01 Z', '2015-10-08T22:02:01Z', 92);
