INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (95, 'Java Software Engineer at Altamira Technologies (McLean, VA)', '&lt;p&gt;Altamira is a top open source technology company in the national security space. Headquartered in McLean, Virginia, Altamira serves the defense, intelligence and homeland security communities by focusing on creating innovative solutions leveraging common standards in architecture, data and security.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you would like to be part of an Agile software development group working on challenging, cutting-edge applications for our customers, send us your resume!&amp;nbsp; In this position you&amp;rsquo;ll be able to create software utilizing technologies such as: Hadoop, Accumulo, Apache Storm, RabbitMQ, OpenNLP, OpenCV, jQuery, AngularJS, D3.js, AWS and many more.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Position Description&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for a software engineer to build scalable back-end features for an emerging open source Big Data analysis and visualization platform called Lumify.&amp;nbsp; This platform offers exciting challenges for processing various types of structured and unstructured data to create a top-notch distributed system.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you&amp;rsquo;re a passionate engineer who enjoys working with talented peers in a result-oriented environment, check out the project at http://lumify.io and contact us to join our team!&lt;/p&gt;', 'McLean, VA', 'http://careers.stackoverflow.com/jobs/94225/java-software-engineer-altamira-technologies', 'Thu, 08 Oct 2015 22:02:01 Z', '2015-10-08T22:02:01Z', 95);
