INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (101, 'Software Build Engineer at RapidAdvance (Bethesda, MD)', '&lt;p&gt;&lt;strong&gt;About the Job&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;RapidAdvance&amp;rsquo;s Development organization is growing, and we are looking for a Software Configuration Manager to join our high-performing team and&amp;nbsp;manage the source code and deployment lifecycle for all projects. This position will be located at&amp;nbsp;our corporate headquarters in Bethesda, Maryland.&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;em&gt;As the Software Configuration Manager (SCM), you will be primary responsible for:&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Establishing SCM policies and procedures&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Managing source code for all projects&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Establishing and maintaining continuous integration process&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Manage release deployment across all functional areas&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Designing, developing, implementing and supporting high-quality software build systems&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;em&gt;A day in this role may also include:&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Introducing&amp;nbsp;new techniques and technologies for new build systems and evolving existing systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintaining and enhancing&amp;nbsp;external interfaces to build systems and information&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Staying abreast of the latest in build technologies that integrate with Microsoft Team Foundation Server (TFS)&amp;nbsp;like Octopus Deploy&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Providing&amp;nbsp;design and implementation guidance - such as&amp;nbsp;best practices, tool use, and quality assurance -&amp;nbsp;and tutoring as necessary to team members&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Developing build scripts and tools for builds, using most appropriate languages and technologies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Integrating&amp;nbsp;automated builds and building artifacts into test environments, unit test harnesses, and code analysis tools&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Developing and managing procedures for data synchronization and validation between the various lifecycle environments.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Identifying and implementing innovative solutions to present and future customer needs; this includes forecasting needs&amp;nbsp;and deriving requirements from customer interactions, internal as well as external&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Developing high-quality designs and code with respect to testability, internationalization, scalability, performance, security, and fault tolerance&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Providing&amp;nbsp;estimates and managing own schedule at component/sub-system level Managing&amp;nbsp;build machines, servers, and build environments, and understanding&amp;nbsp;security concepts and processes for build systems&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Bethesda, MD', 'http://careers.stackoverflow.com/jobs/99978/software-build-engineer-rapidadvance', 'Thu, 08 Oct 2015 22:02:01 Z', '2015-10-08T22:02:01Z', 101);
