INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (125, 'LAMP / PHP Developers wanted! at Global Personals, LLC (Miami Beach, FL) (allows remote)', '&lt;p&gt;Location: Miami Beach, FL&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Remote is &lt;strong&gt;only&lt;/strong&gt; for people outside of Greater Miami (South Florida) area.&amp;nbsp; We are looking to expand our diverse team with PHP and/or Front-End Developers who have great attention to detail, strong communication skills,&amp;nbsp;and good understanding of web accessibility and usability. The primary responsibilities for this position include creating and maintaining custom code for large websites and apps.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Required Experience:&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;PHP - 4+ years of working with medium to large size code bases &amp;nbsp;&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;MySQL - Ability to design normalized database schemas, and optimize queries&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Linux - Comfortable with using a command line interface to perform basic server tasks&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;JavaScript - Solid knowledge of JavaScript and JQuery&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Source Control - Familiarity with Git or SVN&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Preferred Experience:&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Degree in a computer related field, or significant work experience.&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Familiarity with standard web security concepts&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Exposure to various PHP frameworks such as Laravel, Zend, CakePHP, etc.&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Knowledge of some JavaScript frameworks such as Backbone, Angular, React, GWT&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Experience with writing scalable front-end, and back-end code for high traffic websites and mobile apps&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Experience with Memcached, Redis, or other caching technologies&lt;br&gt; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;bull;&amp;nbsp;&amp;nbsp; &amp;nbsp;Experience in other languages such as Java, Python, Golang, Ruby, etc. is a big plus&lt;/p&gt;', 'Miami Beach, FL', 'http://careers.stackoverflow.com/jobs/97348/lamp-php-developers-wanted-global-personals-llc', 'Thu, 08 Oct 2015 21:02:01 Z', '2015-10-08T21:02:01Z', 125);
