INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (133, 'C/C++ developer wanted to join the UltraEdit team at IDM Computer Solutions, Inc. (Hamilton, OH) (allows remote)', '&lt;p&gt;We are IDM Computer Solutions, and we''ve been publishing the world''s most popular text editor, UltraEdit, for over 20 years, and now maintain a portfolio of file management applications on multiple platforms.&amp;nbsp;We love all things text and are looking for a new&amp;nbsp;C/C++ developer &amp;ndash; or outside contractor &amp;ndash; to join our team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The ideal applicant has&amp;nbsp;experience in all aspects of application development to include: C++ design, &amp;nbsp;coding, testing, debugging and documentation of applications to satisfy version requirements. This includes hands-on new development as well as maintenance and&amp;nbsp;enhancement of existing applications.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is a challenging and very rewarding position with a small company and in a family-friendly atmosphere. &amp;nbsp;Working remotely is an option for this position.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Salary is marketplace-driven and based upon your demonstrated skills and experience.&lt;/p&gt;', 'Hamilton, OH', 'http://careers.stackoverflow.com/jobs/99979/c-c-plus-plus-developer-wanted-to-join-the-ultraedit-team-idm-computer-solutions-inc', 'Thu, 08 Oct 2015 21:02:01 Z', '2015-10-08T21:02:01Z', 133);
