INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (144, 'Software Engineer, Network Systems at Fastly (San Francisco, CA)', '&lt;p&gt;&lt;strong&gt;SOFTWARE ENGINEER - NETWORK SYSTEMS&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Fastly is looking for engineers to create the software responsible for configuring, controlling and coordinating our global network. While we operate within the same constraints imposed by network hardware as our competitors, Fastly distinguishes itself by being a software company first and foremost, and we pride ourselves in having always taken a systems perspective to computer networking.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Whether you work on pushing packet performance at the data plane, defining APIs for interacting with network processes at the control plane, or applying decomposition methods to global traffic management is largely immaterial: if you have built a solid understanding of a problem space and are now ready to drive clean slate solutions, you should talk to us.&lt;/p&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99976/software-engineer-network-systems-fastly', 'Thu, 08 Oct 2015 21:02:01 Z', '2015-10-08T21:02:01Z', 59);
