INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (149, 'UX/UI Engineer to Help Build a Team at Learning Objects (Washington, DC)', '&lt;p&gt;&lt;strong&gt;Your Team: &lt;/strong&gt;&lt;span&gt;UX / UI Team&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;We&amp;rsquo;re a team of UX / UI generalists who design experiences and solutions for the most important challenges in education. We live for the little details that surprise and delight teachers and learners. &lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Your Role: &lt;/strong&gt;&lt;span&gt;UX / UI Engineer&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;You&amp;rsquo;ll collaborate with engineers, product managers, and QA engineers to design solutions that optimize for impact, user experience, and efficiency. You&amp;rsquo;ll be responsible for engaging members of the team in the design process and building a strong design culture.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;Understanding the capabilities and constraints of modern web development will be essential as you work with your peers to negotiate and help implement the best solutions.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What will you do?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Work with users and clients to discover their needs and wants&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Approach your work with the mind of a developer and the eye of a designer&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Design, implement, and test interfaces under the agile development methodology&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Work closely with our Java and front-end engineers to contribute to our modern SaaS products&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Continuously improve our products to support the needs of learners, instructors, and content authors&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Contribute to our evolving style-guide&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Leave a lasting mark on the design of our innovative teaching and learning products&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Washington, DC', 'http://careers.stackoverflow.com/jobs/99972/ux-ui-engineer-to-help-build-a-team-learning-objects', 'Thu, 08 Oct 2015 21:02:01 Z', '2015-10-08T21:02:01Z', 149);
