INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (160, 'Senior Software Engineer for a Learning Innovation Partner at Learning Objects (Washington, DC)', '&lt;p&gt;&lt;strong&gt;Your Team: &lt;/strong&gt;&lt;span&gt;Product or Client Solutions Engineering Teams&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;We&amp;rsquo;re creating a disruptive, competency-based, adaptive platform to power the best brands in education. The Product Engineering Team is responsible for continuously improving our underlying platform based on market and customer demands. The Client Solutions Team is responsible for tailoring our award-winning platform to specific client needs.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Your Role: &lt;/strong&gt;&lt;span&gt;Senior Software Engineer- Java/Scala&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;You will apply creativity and advanced mastery of software engineering to effectively design, implement, and support tailored client solutions. &lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What will you do?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Design and develop to solve high risk and/or highly complex business problems using Java and Scala, working on platform technologies that control hundreds of instances of applications&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Develop RESTful API&amp;rsquo;s and rich UI&amp;rsquo;s using client-side JavaScript frameworks&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Execute within an agile framework to meet project, sprint, and production operations commitments effectively and timely&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Lead innovation of engineering processes and technology best practices&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Guide and mentor junior staff&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Washington, DC', 'http://careers.stackoverflow.com/jobs/99970/senior-software-engineer-for-a-learning-learning-objects', 'Thu, 08 Oct 2015 21:02:01 Z', '2015-10-08T21:02:01Z', 149);
