INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (166, 'Senior Software Engineer - C/C++/C# at BitTorrent, Inc. (San Francisco, CA)', '&lt;p&gt;At &lt;a href="http://www.bittorrent.com/" rel="nofollow" rel="nofollow" rel="nofollow"&gt;BitTorrent&lt;/a&gt;, our mission is to build a better Internet. We are building a web that works the way it should, person to person, powered by individuals, not corporations. We want to drive the development of the distributed web by building tools to easily publish, discover and consume content. &lt;a href="http://project-maelstrom.bittorrent.com/" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Project Maelstrom&lt;/a&gt; is a major part of this mission - a browser with a distributed protocol that will open a better future for publishers, creators, and users.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for a Senior Software Engineer to help us build this future.&lt;/p&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99961/senior-software-engineer-c-c-plus-plus-c-sharp-bittorrent-inc', 'Thu, 08 Oct 2015 21:02:01 Z', '2015-10-08T21:02:01Z', 166);
