INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (173, 'Software Engineering Team Lead for a Growing Edtech Organization at Learning Objects (Washington, DC)', '&lt;p&gt;&lt;strong&gt;Your Team: &lt;/strong&gt;&lt;span&gt;Product Engineering&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;We&amp;rsquo;re creating a disruptive, competency-based, adaptive platform to power the best brands in education. The Product Engineering team is responsible for continuously improving our underlying platform based on market and customer demands. In that capacity, our team has the exciting role of connecting all of our clients on a backbone that executes on a cohesive, focused vision.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Your Role: &lt;/strong&gt;&lt;span&gt;Software Engineering Team Lead - Java/Scala&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;You will apply creativity and advanced mastery of software engineering to effectively design, implement, and support tailored client solutions. Most importantly, you&amp;rsquo;ll use your people management skills to build and continuously motivate high-functioning teams, implement meaningful staff roles and responsibilities, and cultivate your employees&amp;rsquo; talents.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What will you do?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Build products / write code&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Work closely with both our engineers and our UI designers, contribute to our modern, mobile-enabled web apps&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Work with internal and external stakeholders to shape the product roadmap&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Lead your clients and projects&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Work with users and clients to discover their needs and desires&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Design, implement and test new features using the agile development methodology &lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Scope and break down work for implementation&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Provide technical leadership&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Apply your past experiences to help us scale and expand our engineering organization&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Disseminate engineering best practices per LO guidelines to reporting teams&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Mentor team members to maximize their potential and steer their career growth&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;/ul&gt;', 'Washington, DC', 'http://careers.stackoverflow.com/jobs/99966/software-engineering-team-lead-for-a-growing-learning-objects', 'Thu, 08 Oct 2015 21:02:01 Z', '2015-10-08T21:02:01Z', 149);
