INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (182, 'Not Just Another Java Developer at Learning Objects (Washington, DC)', '&lt;p&gt;&lt;strong&gt;Your Team: &lt;/strong&gt;Product or Client Solutions Engineering Teams&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We&amp;rsquo;re creating a disruptive, competency-based, adaptive platform to power the best brands in education. &amp;nbsp;The Product Engineering Team is responsible for continuously improving our underlying platform based on market and customer demands. The Client Solutions Team is responsible for tailoring our award-winning platform to specific client needs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Your Role: &lt;/strong&gt;Software Engineer - Java/Scala&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will apply creativity and intermediate mastery of software engineering to effectively design, implement, and support our proprietary platform.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What will you do?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Solve high risk and/or highly complex business problems using Java and Scala, working on platform technologies that control hundreds of instances of applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop RESTful APIs and rich UIs using client-side JavaScript frameworks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Execute within an agile framework to meet project commitments effectively and timely&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support innovation of engineering processes and technology best practices&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Washington, DC', 'http://careers.stackoverflow.com/jobs/99960/not-just-another-java-developer-learning-objects', 'Thu, 08 Oct 2015 20:02:01 Z', '2015-10-08T20:02:01Z', 149);
