INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (183, 'Principal Engineer/Team Lead - Back End Java Development at Tillster, Inc. (San Diego, CA)', '&lt;p&gt;&lt;strong&gt;Principal Engineer/Team Lead - Back End Java&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;em&gt;RELOCATION ASSISTANCE IS AVAILABLE&lt;/em&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;em&gt;TRANSFER OF EXISTING VALID H1-B VISA WILL BE CONSIDERED&lt;/em&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;About the position:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;A great opportunity exists for&amp;nbsp;&lt;strong&gt;Principal Software Engineer/Team Lead&amp;nbsp;&lt;/strong&gt;who is a hands-on coding guru for our Enterprise Mobile/Web Ordering Systems group.&amp;nbsp; You&amp;rsquo;ll be engaged in the design and hands-on coding of our Highly Scalable, Highly Availability Enterprise Mobile Services/Applications.&amp;nbsp; These Services/Applications are hosted on state-of-the-art Cloud Service providers in multiple geographies.&amp;nbsp; We are looking for battle-tested Sr. Software Engineers who has dealt with Enterprise and Internet-level issues with regards to scalability, high availability, and performance tuning particularly for delivery of mobile applications. &amp;nbsp;Also requires previous experience as a team lead.&lt;/p&gt;', 'San Diego, CA', 'http://careers.stackoverflow.com/jobs/54739/principal-engineer-team-lead-back-end-java-tillster-inc', 'Thu, 08 Oct 2015 20:02:01 Z', '2015-10-08T20:02:01Z', 140);
