INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (190, 'JMP Software Developer at SAS (Cary, NC)', '&lt;p&gt;JMP is an independent division of SAS producing interactive, graphical, statistical discovery software. As a Software Developer with JMP, you will be responsible for writing and maintaining C++ code for a large desktop application with a timed release cycle. In particular, you will be responsible for maintaining and developing graphical infrastructure code to enable statistical visualizations, including interfaces at the C++ API level, scripting level, and user interaction level. You will work autonomously as part of a small team.&lt;/p&gt;', 'Cary, NC', 'http://careers.stackoverflow.com/jobs/99958/jmp-software-developer-sas', 'Thu, 08 Oct 2015 20:02:01 Z', '2015-10-08T20:02:01Z', 190);
