INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (192, 'Ruby Application Engineer at Fastly (San Francisco, CA)', '&lt;p&gt;&lt;strong&gt;RUBY APPLICATION ENGINEER&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;A Fastly Application Engineer is responsible for both improving upon and maintaining the various components that make up Fastly''s customer-facing Configuration API. The Configuration API is one of Fastly''s features that sets us apart from our competition. We''re looking for candidates excited about the prospect of building highly available, customer-facing, globally distributed control systems.&lt;/p&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99959/ruby-application-engineer-fastly', 'Thu, 08 Oct 2015 20:02:01 Z', '2015-10-08T20:02:01Z', 59);
