INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (204, 'Sr. Platform/Java Engineer - Westminster, CO at Gorilla Logic, Inc. (Boulder, CO)', '&lt;p&gt;&lt;strong&gt;Gorilla Logic &amp;ndash; Platform Dev Engineer&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Gorilla Logic is working with our long time partner to assist in developing cutting edge cloud-based systems to manage a huge archive of imagery. We are in need of a senior level developer who is motivated and enthusiastic to build elegant, efficient and simple cloud based systems. This person will assist in creating a core functionality where it doesn&amp;rsquo;t exist. The team you will join has 2 objectives: build and deploy robust cloud based systems, develop tools to enable 3&lt;sup&gt;rd&lt;/sup&gt; party developers to build and deploy algorithms on the platform and develop the technology to enable future cloud and big-data access to imagery and computation.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work as part of the platform team, building new features, deploying them, and rolling them out to customers.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with our scientists &amp;amp; engineers to understand &amp;amp; move their algorithms onto the platform.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Troubleshoot and performance- tune end-to-end systems within a highly-distributed application environment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop prototypes and tools, with focus on simplicity &amp;amp; ease of use.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work in a collaborative team environment, co-designing and co-implementing key applications &amp;amp; components.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;The Details&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Type of Employment: W2, C2C, 1099&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Length: Long term project estimated at 1+year&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Location : Westminster, CO (on-site work only. No opportunities for remote work)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Availability: ASAP to 2 weeks&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Perks: Amazingly supportive team, interesting and high profile work. 401k with matching, health dental and vision insurance for W2 employees.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;NO THIRD PARTY RECRUITERS&lt;/p&gt;', 'Boulder, CO', 'http://careers.stackoverflow.com/jobs/99178/sr-platform-java-engineer-westminster-co-gorilla-logic-inc', 'Thu, 08 Oct 2015 20:02:01 Z', '2015-10-08T20:02:01Z', 204);
