INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (207, 'Ruby On Rails Developer at Computer Bits, Inc. (Chicago, IL)', '&lt;p&gt;We are looking for a Ruby on Rails developer with a minimum of 3-4 years experience. You would be working with a small team of developers to complete a complex membership/certification application which utilizes Ruby on Rails 4, Bootstrap 3, MySQL, Amazon SQS and is deployed using Docker.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Your responsibilities would include:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Building or updating application features.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Using RSpec to add unit/feature tests for non-tested code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Debug and troubleshoot issues on the live application.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/99955/ruby-on-rails-developer-computer-bits-inc', 'Thu, 08 Oct 2015 20:02:01 Z', '2015-10-08T20:02:01Z', 207);
