INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (220, 'PHP / LAMP Senior Developer at I See Me! LLC (Eden Prairie, MN) (allows remote)', '&lt;p&gt;We are only accepting applications from US Citizens living in the Twin Cities area of Minneapolis/St. Paul, MN.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is a lead software engineer position in an entrepreneurial, fast-paced eCommerce company that develops innovative technologies. This position involves 75% writing code&amp;nbsp;and 25% managing 3-4 other software engineers. You will be on the leadership team helping to drive the company''s strategy, and you will&amp;nbsp;interface with other members of the company''s leadership team to define how technology can enable the company''s strategic goals.&amp;nbsp;&amp;nbsp;You will&amp;nbsp;lead various IT initiatives including writing innovative code for new product development, enhancing the user experience on our Magento website, integrating our systems with retailers to receive orders, and continuously improving the IT infrastructure. This person will also lead the effort to provide&amp;nbsp;operational support for I See Me!&amp;rsquo;s IT infrastructure, improve and maintain the IT infrastructure, and improve technical processes in response to business needs.&lt;/p&gt;', 'Eden Prairie, MN', 'http://careers.stackoverflow.com/jobs/99954/php-lamp-senior-developer-i-see-me-llc', 'Thu, 08 Oct 2015 20:02:01 Z', '2015-10-08T20:02:01Z', 220);
