INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (247, 'Data Engineer / Data Scientist at Grouplend (Vancouver, BC, Canada)', '&lt;p&gt;With great data comes great responsibility.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We&amp;rsquo;re Grouplend. We&amp;rsquo;re a data-driven, technology-enabled online lending platform. That&amp;rsquo;s a fancy way of saying we sell kick-ass loans on the internet. We&amp;rsquo;re on a mission to help Canadians avoid the traps of predatory lenders and outdated offerings from banks.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We&amp;rsquo;re improving on traditional underwriting models through high levels of scalable automation, machine learning algorithms, and other &amp;ldquo;big &amp;shy;data&amp;rdquo; analysis to lower the cost of borrowing and improve the user experience for our clients.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Data is at the core of our DNA and we are looking for an experienced lead Data Engineer/Data Scientist to develop the architecture that helps analyze and process data for real time machine learning for risk, fraud and marketing purposes.&lt;/p&gt;', 'Vancouver, BC, Canada', 'http://careers.stackoverflow.com/jobs/98541/data-engineer-data-scientist-grouplend', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 247);
