INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (252, 'DevOps Engineer at uShip (Austin, TX)', '&lt;p&gt;&lt;strong&gt;Job Overview&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Do you enjoy working with a wide array of technologies to solve important business, scalability, and performance problems?&amp;nbsp; Do you like pushing the boundaries of new technologies and leveraging existing infrastructure to build cutting edge products?&amp;nbsp; We are looking for a talented &lt;strong&gt;DevOps Engineer &lt;/strong&gt;to join the ranks of the infrastructure group responsible for the architecture and deployment of uShip.com.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What you''ll be doing&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Managing a continuous integration/deployment environment with multiple pushes per day.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working with architects and developers to solve operational issues&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continuing to develop our ELK stack for site-wide monitoring and alerting&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working with stakeholders to develop a more efficient deployment platform to support evolving needs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Monitoring the deployment process and correcting any issues, bottlenecks, and performance problems&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Austin, TX', 'http://careers.stackoverflow.com/jobs/99948/devops-engineer-uship', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 252);
