INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (259, 'Software Engineer - .NET/C#/Web, Desktop, Mobile at Applied Visions, Inc. (Northport, NY)', '&lt;p&gt;&lt;span&gt;We are seeking an&amp;nbsp;&lt;/span&gt;&lt;strong&gt;experienced software engineer&lt;/strong&gt;&lt;span&gt;&amp;nbsp;with the capability to lead a project through the full software lifecycle. You will play a significant role in developing exciting new applications in the areas of information security, information visualization, user interface design, financial applications, transportation, and healthcare. You will work closely with clients to develop their vision, gather requirements, shape projects, define tasks and build solutions. This is a hands-on role where an individual with strong analytical and communication skills and a keen focus on the client will succeed.&lt;/span&gt;&lt;br&gt;&lt;br&gt;&lt;strong&gt;You Will:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work on end-to-end management of all phases of the product development cycle from product definition and design, through implementation including: requirements gathering, testing, debugging and any rework required by those phases.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work on architecture, design and product definition that leads and influences decisions and outcomes related to project direction.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop high quality, deliverable software with little management intervention or oversight.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Partner with clients and other team members as a technical advisor and resource to other engineers and across functions.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Northport, NY', 'http://careers.stackoverflow.com/jobs/99928/software-engineer-net-c-sharp-web-desktop-mobile-applied-visions-inc', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 259);
