INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (262, 'Sr System Developer - Automation at Fidelity (Durham, NC)', '&lt;p&gt;The PWI Software Release Management group has a position available for a qualified Software Automation Engineer. A software automation engineer on this team will have the opportunity to work with business and systems teams to identify areas for automation, develop self-service capability using interactive User Interface, &amp;nbsp;develop and test automation routines, document and execute test plans to ensure new automation components satisfy business and systems requirements. You will also participate in project deliveries using Agile Development methodology, in addition to the usual waterfall methods.&amp;nbsp;Current environment includes: Java, JSP, JavaScript, Nodes, Chef, Tomcat, Oracle, MySQL, VMware, Atlessian Stash/Git, Jenkins, NexusPro and Linux &amp;amp; Windows.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Primary Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;PWI Release Management team supports over 2000 developers distributed across the Globe.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;This team is required to support and remediate any SCM related issues that the development may be experiencing with specific focus on avoiding down time.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Candidate will be working primarily in driving process automation, self-service capabilities and workflow automation to align SCM, build, promotion, release and deployment to support agile development, Continuous Integration and Continuous Delivery.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Programming experience with Java, HTML5, Perl and JSP/JavaScript for building self-service and automation will be required.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with Linux and UNIX environment along with script development in Perl/ksh/Chef/Groovy will be required.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Candidates should have familiarity with builds using ANT, Maven and Nexus.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;He or she will support developers in implementing the policies by writing the Triggers, Hooks in Perl, Shell and JavaScript.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Durham, NC', 'http://careers.stackoverflow.com/jobs/97339/sr-system-developer-automation-fidelity', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 262);
