INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (265, 'Principal Architect / Lead Developer, Web CMS at Fidelity (Cary, NC)', '&lt;p&gt;The mission&amp;nbsp;of Fidelity Charitable Services (FCS) is to&amp;nbsp;be the premier provider of charitable solutions, offering innovative ways to make giving easy and satisfying for our customers.&amp;nbsp; FCS offers the Fidelity Charitable Gift Fund&lt;sup&gt;SM&lt;/sup&gt;, the nation''s largest donor-advised fund.&amp;nbsp; Since 1991, the Gift Fund has introduced tens of thousands of donors to the benefits &amp;mdash; and increased charitable impact &amp;mdash; of using a donor-advised fund to meet their philanthropic goals.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Position description&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As architect, Content Management Systems, you will play a lead role in the development of a new Drupal based CMS system to support Fidelity Charitable&amp;rsquo;s growing set of redesigned digital properties. This will involve working closely with product owners, experience designers, and developers to shape, and lead the initial development of the new CMS platform and require a comprehensive understanding of content management, Drupal, development of rich experiences using the latest web technologies and standards.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Key responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Architectural design for the integrated Drupal CMS (i.e. systems integration, module design, content structure, themes / front-end integration, physical deployment etc.)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Hands on framework development of CMS components, and any necessary customization / integration&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working with front-end development and other technology teams to integrate /migrate to the CMS&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Liaison with 3&lt;sup&gt;rd&lt;/sup&gt; party vendors in supporting and upgrading the CMS platform&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Supporting, educating / mentoring business users and other stakeholders on CMS and Drupal&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Cary, NC', 'http://careers.stackoverflow.com/jobs/97337/principal-architect-lead-developer-web-cms-fidelity', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 262);
