INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (267, 'Junior to Mid Level Front End Developer - Java, Angular, JSON APIs, and Web Serv at Fidelity (Durham, NC)', '&lt;p&gt;The Fidelity Institutional Technology group at Fidelity Investments&amp;nbsp;has an exceptional opportunity&amp;nbsp;for a Software Developer / Engineer. This role will participate in the design, development, implementation and support of mission critical trading focused web applications and financial tools. Under moderate supervision, conduct the analysis, design and development of software to support a robust trading and supporting platform, contributing to the overall success of projects as they move through the different phases of the project life cycle.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Your knowledge will be called upon to meet guidelines and standards that will allow our team to continue to deliver important and vital solutions for our customers.&amp;nbsp; Our projects involve developing functions and tools used by Registered Investment Advisors (RIAs) and Correspondents.&amp;nbsp; Our web applications use modular JavaScript frameworks (such as Angular, Backbone, etc.), JSON APIs, Java and Web Services.&amp;nbsp; You should be able to understand technical designs at the application level, define strategies and work closely with fellow developers.&amp;nbsp; You must be able to describe problems and recommend solutions to project leaders.&amp;nbsp; You must have great communication skills, be a team player, and have an aptitude for learning new technologies.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Primary Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Designing, developing, and implementing web applications in a dynamic financial services environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contributing effectively at many different points in a project life cycle as an individual contributor&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Adhering to project timelines, addressing and resolving technical issues&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Coordinating functional/technical group discussions with analysts and developers under moderate supervision&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Delivering on both projects and support tasks in a dynamic environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Developing concise and clear technical design documents based on analysis of business requirements&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Durham, NC', 'http://careers.stackoverflow.com/jobs/97336/junior-to-mid-level-front-end-developer-java-fidelity', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 262);
