INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (269, 'Senior SQL DBA at uShip (Austin, TX)', '&lt;p&gt;&lt;strong&gt;Job Overview&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Do you enjoy working with a wide array of technologies to solve important business, scalability, and performance problems?&amp;nbsp; Do you like pushing the boundaries of new technologies and leveraging existing infrastructure to build cutting edge products?&amp;nbsp; We are looking for a talented &lt;strong&gt;Senior Database Administrator &lt;/strong&gt;to join the ranks of the infrastructure group responsible for the architecture and deployment of uShip.com.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What you''ll be doing&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Mentoring other members of the development team and junior team members on database design and optimization.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be a key player in the continuous integration cycle, making sure data architecture concerns are addressed as part of the process.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Lead the development and implementation of all data systems to support scalability, availability, and performance.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design disaster recovery solutions to make sure the site is always protected.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Plan for future growth and work with IT on implementing hardware to meet those goals.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Austin, TX', 'http://careers.stackoverflow.com/jobs/99930/senior-sql-dba-uship', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 252);
