INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (290, 'Senior Software Engineer at American Express (New York, NY)', '&lt;p&gt;American Express TRS&lt;br /&gt;seeks a Senior Software Engineer to develop and test, among others, big data&lt;br /&gt;software, including ongoing refactoring of code and drive continuous&lt;br /&gt;improvement in code structure and quality.&amp;nbsp;&lt;br /&gt;Work would involve writing code, API specifications, conducting code&lt;br /&gt;review and testing in ongoing sprints.&amp;nbsp;&amp;nbsp;&lt;br /&gt;Drive proof of concepts and the usage of automation tools.&amp;nbsp; Function as a core member of an Agile team&lt;br /&gt;driving user story analysis and elaboration, design, and development of&lt;br /&gt;software applications. Work on specific platforms and products or as part of a&lt;br /&gt;dynamic resource pool assigned to projects based on demand and business&lt;br /&gt;priority.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Qualifications&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Position requires a&lt;br /&gt;Bachelors degree in Computer Science, Computer Engineering, or a related&lt;br /&gt;technical field, followed by 5 years of progressively responsible experience&lt;br /&gt;with application design, software development, and automated testing.&amp;nbsp; Experience must include collaborating with&lt;br /&gt;the business to drive business and data requirements as well as interpreting&lt;br /&gt;technical and business objectives/challenges and develop appropriate&lt;br /&gt;solutions.&amp;nbsp; Demonstrated experience with&lt;br /&gt;J2EE frameworks, including Springs/ Struts, and complex database concepts,&lt;br /&gt;including join strategy, performance optimization, and compression, is&lt;br /&gt;required.&amp;nbsp; Experience with TOGAF and SOA&lt;br /&gt;architecture design concepts, and application deployment and configuration of&lt;br /&gt;web servers is required.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;Job location: New York, New York.&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;i&gt;American Express is an equal&lt;br /&gt;opportunity employer and makes employment decisions without regard to race,&lt;br /&gt;color, religion, sex, sexual orientation, gender identity, national origin,&lt;br /&gt;protected veteran status, disability status, or any other status protected by law.&lt;/i&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;i&gt;Click here to view the &lt;/i&gt;&lt;a href="https://mail.aexp.com/owa/redir.aspx?C=jN7Rv2rLtkaFYf06GKW4zDIdei0-G9EIV10xFDLBDejBvNLrvHpukI5bweJRXQqIUqfLMQrFA24.&amp;amp;URL=http://www.dol.gov/ofccp/regs/compliance/posters/ofccpost.htm" rel="nofollow" rel="nofollow"&gt;&lt;i&gt;EEO is the Law&lt;/i&gt;&lt;/a&gt;&lt;i&gt; poster. If the link does not work, please copy and paste&lt;br /&gt;the following URL in a new browser window:&lt;/i&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;a href="http://www.dol.gov/ofccp/regs/compliance/posters/ofccpost.htm" rel="nofollow" rel="nofollow"&gt;&lt;i&gt;http://www.dol.gov/ofccp/regs/compliance/posters/ofccpost.htm&lt;/i&gt;&lt;/a&gt;&lt;br /&gt;&lt;br /&gt;ReqID: 15015954', 'New York, NY', 'http://careers.stackoverflow.com/jobs/99357/senior-software-engineer-american-express', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 271);
