INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (309, 'Security Engineer at Braintree (Chicago, IL) (allows remote)', '&lt;p&gt;&lt;span&gt;About Us:&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;At Braintree, we&amp;rsquo;re building products that make payments easier&amp;mdash;so easy that they fade into the background, making entirely new kinds of interactions possible. Our full-stack payment platform lets companies build their own experiences and then scale their businesses around the globe. We have developer friendly APIs built to integrate with various platforms - Ruby, Python, Java, PHP, Node.js and .NET - and we also have SDKs for iOS, Android, and Windows.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;Our technology fuels companies like Github, 37Signals, Uber, Airbnb, OpenTable. Heroku and more.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;We''re also in a unique position to greatly impact how merchants and customers interact. The product teams at Braintree work together with Venmo, the mobile payments company we acquired in 2012. Our joint offering, Venmo Touch, gives our users one touch purchasing within apps, eliminating the need to retype your credit card number inside each app you download. Recently acquired by PayPal, we''re now in an even greater position to change the way people pay.&lt;/span&gt;&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/55219/security-engineer-braintree', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 309);
