INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (318, 'Sr. Artificial Intelligence Game Programmer at Daybreak Game Company, LLC (Austin, TX)', '&lt;p&gt;Daybreak Games is now hiring for a Senior Artificial Intelligence Programmer at our Austin Studio.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The only thing better than playing killer video games is getting paid to play them while you work for the company that creates them!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Daybreak Games is recruiting for a Sr. AI Programmer to work with a team of highly motivated and talented programmers, artists, and designers in Austin Texas.&amp;nbsp; The DC Universe Online team creates action-packed, physics based massively multiplayer online role-playing games (MMORPG) based upon the fictional universe of DC Comics.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The team needs an additional Game Programmer to fill a senior and integral role in the design and implementation of these Artificial Intelligence (AI) game systems and features, both in terms of individual character behavior as well as higher level logic to control the coordination of multiple game entities and game systems.&amp;nbsp; This role will have primary responsibility for all AI systems used in both server and client sides, and will have a significant role in how we utilize, advance, and expand those systems in an upcoming, unannounced title that will be developed in the Daybreak Austin studio, as well as some support for DC Universe Online.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Responsibilities:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Passion for writing solid, reliable, efficient code and highly scalable systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design, create, optimize, and maintain cross-platform systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ability to work efficiently and independently on components and then collaboratively when it&amp;rsquo;s time to bring the pieces of the teams efforts together&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Works well under pressure and can handle multiple tasks&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Seeking a seasoned programmer that knows how to appropriately use advanced and sophisticated methods of artificial intelligence within the budgets and constraints of a real time game simulation, to create believable and (most importantly) FUN, computer controlled characters and gameplay experiences.&lt;/p&gt;', 'Austin, TX', 'http://careers.stackoverflow.com/jobs/99925/sr-artificial-intelligence-game-programmer-daybreak-game-company-llc', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 318);
