INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (323, 'Windows/Linux Systems Administrator at uShip (Austin, TX)', '&lt;p&gt;&lt;strong&gt;Job Overview:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;uShip is seeking a talented individual to join our team as a full time &lt;strong&gt;Windows/Linux Systems Administrator&lt;/strong&gt;. This individual will be a part of the uShip Infrastructure team.&amp;nbsp; Locally, our office has roughly 180 employees and is continuing to grow. If you are a motivated, smart, and driven individual who wants to work in an energetic and entrepreneurial environment with outstanding career and growth opportunities, then this is the opportunity for you!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Job Responsibilities&lt;/strong&gt;:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, deploy, and configure server infrastructure in a customer facing internet environment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support of Active Directory, Exchange, application server support, and OS support&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Management of VMware in HA enterprise environment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Basic scripting for automation of routine tasks for both Windows and Linux Server&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support of Linux servers&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Austin, TX', 'http://careers.stackoverflow.com/jobs/99927/windows-linux-systems-administrator-uship', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 252);
