INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (327, 'Software Engineering Tool Administrator at Fidelity (Cary, NC)', '&lt;p&gt;The Fidelity Institutional Technology Software Configuration Management (FITSCM) group has a position available for a qualified software development tools administrator. The role will include helping FIT application teams learn about and implement Continuous Integration, Continuous Deployment, Continuous Testing, and DevOps practices.&amp;nbsp; The role includes fostering best practices and automation in the areas of version control, application builds, staging, and deployment to various environments.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Primary Responsibilities&lt;/strong&gt;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;FIT SCM team supports over 700 developers distributed across the Globe. This team administers services for the development community to provide the tools necessary for fast-paced delivery of quality applications.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Candidates should have working knowledge in GIT, Jenkins, and Nexus administration to support development practices.&amp;nbsp; Candidates should have experience with Linux and basic UNIX tools/script development in Perl/ksh, and working knowledge of distributed development practices.&amp;nbsp; Familiarity with Rational Team Concert is a plus.&lt;/p&gt;', 'Cary, NC', 'http://careers.stackoverflow.com/jobs/97334/software-engineering-tool-administrator-fidelity', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 262);
