INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (343, 'Experienced iOS / Android Developer - Copenhagen, Stockholm, San Francisco at Jayway (Copenhagen, Denmark)', '&lt;p&gt;As a mobile developer at Jayway, you are an expert in Android and/or iOS development.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You value creating meaningful digital user experiences and good software craftsmanship and you are familiar with the latest technology and frameworks.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our corporate culture is based on sharing knowledge, helping each other grow and learn. We arrange the &amp;Oslash;redev conference, as well as numerous internal mini-conferences throughout the year.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will join our offices in either Copenhagen Denmark, Stockholm Sweden or San Francisco USA.&lt;/p&gt;', 'Copenhagen, Denmark', 'http://careers.stackoverflow.com/jobs/98136/experienced-ios-android-developer-copenhagen-jayway', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 343);
