INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (345, 'Front End Developer (Javascript Engineer) at uShip (Austin, TX)', '&lt;p&gt;&lt;strong&gt;What you&amp;rsquo;ll be doing:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Developing web applications using MVC, HTML5, JavaScript and CSS3&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working on a 4-8 member team to continuously build and integrate new features.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working with our product and UX teams to prototype and develop highly interactive components&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Austin, TX', 'http://careers.stackoverflow.com/jobs/99923/front-end-developer-javascript-engineer-uship', 'Thu, 08 Oct 2015 19:02:01 Z', '2015-10-08T19:02:01Z', 252);
