INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (357, 'Senior Web Services Developer at Innate, Inc. (Washington, DC)', '&lt;p&gt;Interactive agency is seeking a talented&amp;nbsp;senior developer and architect fluent in building and maintaining robust, modern web services. In addition to having experience with ASP.NET MVC, Web API, and SQL Server, the ideal candidate should also have experience with open source technologies such as Ruby, MySQL/Postgres, and some NoSQL experience with MongoDB or Amazon''s DynamoDB.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Lead new development projects for app development, web services, and database architecture for a wide array of clients&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Architect and develop new features for a JSON based eCommerce web service and analytics with hundreds of GBs of data&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Design new APIs with ease of use and security in mind&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Deploy to production and staging often (we use the GitHub Flow methodology)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Strong testing and QA mindset&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Managing a hybrid and polyglot system (.NET, Ruby on Rails, Postgres, MongoDB, SQL Server, Redis, and Sidekiq) on Amazon Web Services&lt;/p&gt;', 'Washington, DC', 'http://careers.stackoverflow.com/jobs/85518/senior-web-services-developer-innate-inc', 'Thu, 08 Oct 2015 18:02:01 Z', '2015-10-08T18:02:01Z', 357);
