INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (359, 'Senior .NET Developer at uShip (Austin, TX)', '&lt;p&gt;&lt;strong&gt;What you&amp;rsquo;ll be doing:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Developing web applications and using Microsoft .Net and open source technologies.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Creating and enhancing product features in MVC 4 with C# and ASP.Net Webforms using VB.Net&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working directly with Product Managers, Developers and Quality Assurance to deliver new products and features.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Modifying existing applications and features to improve performance, code readability etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Communicating your ideas across a team of 30 developers&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Austin, TX', 'http://careers.stackoverflow.com/jobs/87483/senior-net-developer-uship', 'Thu, 08 Oct 2015 18:02:01 Z', '2015-10-08T18:02:01Z', 252);
