INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (362, 'Senior Software Engineer at CreativeLive (San Francisco, CA)', '&lt;p&gt;&lt;span&gt;&lt;strong&gt;Join our dynamic team of innovators hard at work building the future of education.&lt;/strong&gt; From our offices in Seattle and San Francisco, we make it possible for people everywhere to learn what they want and do what they love.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Founded with the belief that world-class education can (and should) be available to everyone, CreativeLive connects photographers, crafters, musicians, and entrepreneurs of all walks of life with top-notch learning from industry leaders.&lt;/strong&gt; Whether a weekend warrior or the newly full-time freelancer, CreativeLive gives students the tools and the encouragement to make it happen, whatever "it" might be for them.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;CreativeLive is looking for &lt;strong&gt;talented senior engineers&lt;/strong&gt; to join our growing technical team in our&amp;nbsp;San Francisco or&amp;nbsp;Seattle office. You will be forging a new frontier in learning applications, leveraging and contributing to open source technology.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our platform is built on&amp;nbsp;&lt;strong&gt;Node.js&amp;nbsp;&lt;/strong&gt;and &lt;strong&gt;Angular.js&lt;/strong&gt;, and the ideal candidate will be excited about&amp;nbsp;applying&amp;nbsp;their strong&amp;nbsp;coding and architecture skills to an end-to-end&amp;nbsp;&lt;strong&gt;JavaScript&lt;/strong&gt; stack.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Along with your fellow engineers, you''ll be working with design and product to continuously improve the CreativeLive site and internal tools by:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;iterating on prototypes&lt;/li&gt;&lt;br /&gt;&lt;li&gt;architecting modules and applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;refactoring existing parts of the site&lt;/li&gt;&lt;br /&gt;&lt;li&gt;extending our content management system&lt;/li&gt;&lt;br /&gt;&lt;li&gt;implementing data visualization dashboards&lt;/li&gt;&lt;br /&gt;&lt;li&gt;interfacing with&amp;nbsp;(and creating) web services&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;improving the scalability and fault tolerance of our distributed systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;actively contributing to refining our Agile development processes&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Some of the things that would make you a great fit for our team:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;You embody creativity, positivity, and&amp;nbsp;exploration, and are inspired by our mission.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You are an active contributor in the technical&amp;nbsp;community.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You care deeply about code quality, performance and execution.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You understand that software engineering is a team sport.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You''re passionate about learning and sharing what you''ve learned.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What we''re looking for:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;5+ years professional experience as a software engineer.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Expertise in modern web development.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Total comfort with&amp;nbsp;Unix/Linux.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Mastery of basic Computer Science&amp;nbsp;fundamentals.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Bonus points for:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;A 4-year accredited technical degree, or equivalent&amp;nbsp;professional&amp;nbsp;experience (not just a coding bootcamp, please)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Prior work experience in an agile environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong&amp;nbsp;programming skills in&amp;nbsp;a variety of languages&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Mad skillz&amp;nbsp;with any tech in our stack: Node, Angular, Mongo, AWS, Docker, Kafka, Redshift, etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Deep relevant domain expertise in an area such as networking, data engineering, devops, video streaming, ecommerce, mobile, (both iOS and Android), containerization, scalability, analytics, etc.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99921/senior-software-engineer-creativelive', 'Thu, 08 Oct 2015 18:02:01 Z', '2015-10-08T18:02:01Z', 352);
