INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (369, 'Senior Developer PHP/Java Script/MySQL (m/w) at Onlineprinters GmbH (Neustadt an der Aisch, Deutschland)', '&lt;p&gt;&lt;strong&gt;Ihre Herausforderung:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Sie erweitern das Entwicklerteam indem Sie bei der Planung, Entwicklung und Betreuung unterschiedlichster betriebsorientierter L&amp;ouml;sungen mitwirken.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Ihr Profil:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Mind. 5 Jahre professionelle Erfahrung mit objektorientiertem PHP 5 sowie sehr gute Kenntnisse in mindestens zwei der folgenden Bereiche:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Symfony 1 und/oder 2&lt;/li&gt;&lt;br /&gt;&lt;li&gt;SQL (bevorzugt MySQL)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;JavaScript, objektorientiert&lt;/li&gt;&lt;br /&gt;&lt;li&gt;AngularJS&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Daneben setzen wir den sicheren Umgang mit HTML 5 / CSS 3, Git o.&amp;auml; und Xdebug o.&amp;auml;. voraus.&amp;nbsp; Wir besch&amp;auml;ftigen uns mit einer Vielzahl von Tools und freuen uns &amp;uuml;ber Ihre Erfahrung in diesen Bereichen: Composer, Bower, NodeJs, RESTful, SOAP, RabbitMQ, Redis, Elasticsearch, Jira, jQuery, Grunt, Gulp, Phing, Jenkins, Puppet, Ansible, Bootstrap, Vagrant, VM Ware, Linux, Apache, Nginx, Logstash, Less&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Wir bieten:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Einen sicheren Arbeitsplatz in einem stark wachsenden Unternehmen, mit der M&amp;ouml;glichkeit, diesen aktiv mit zu gestalten. Einen Arbeitsvertrag mit bis zu 13,5 Monatsgeh&amp;auml;ltern und 30 Tagen Jahresurlaub sowie verm&amp;ouml;genswirksame Leistungen. Wir freuen uns auf Ihre aussagekr&amp;auml;ftigen Bewerbungsunterlagen (inkl. Gehaltsvorstellung und Eintrittsdatum) vorzugsweise per E-Mail an die unten genannte Adresse.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Onlineprinters GmbH&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Personalabteilung Frau R&amp;ouml;ck&lt;br&gt; Rudolf-Diesel-Str. 10&lt;br&gt; 91413 Neustadt a. d. Aisch&lt;br&gt;bewerbung(at)diedruckerei.de&lt;br&gt;&lt;a href="http://www.diedruckerei.de/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;www.diedruckerei.de&lt;/a&gt;&lt;/p&gt;', 'Neustadt an der Aisch, Deutschland', 'http://careers.stackoverflow.com/jobs/92326/senior-developer-php-java-script-mysql-m-w-onlineprinters-gmbh', 'Thu, 08 Oct 2015 18:02:01 Z', '2015-10-08T18:02:01Z', 369);
