INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (376, 'Web Developer at Travelzoo (Mountain View, CA)', '&lt;p&gt;Travelzoo is looking for a Web Developer with the ability to design and build scalable web applications. The ideal candidate will have development experience with the Microsoft .NET / MS SQL stack, as well knowledge of front-end technologies like CSS and JavaScript. The position is full-time and based in Mountain View, CA.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Develop software solutions for the Travelzoo Website.&lt;br&gt; &amp;bull; Maintain and support our internal applications.&lt;br&gt; &amp;bull; Build and implement new web applications.&lt;br&gt; &amp;bull; Add creative input to existing projects.&lt;/p&gt;', 'Mountain View, CA', 'http://careers.stackoverflow.com/jobs/99919/web-developer-travelzoo', 'Thu, 08 Oct 2015 17:02:01 Z', '2015-10-08T17:02:01Z', 376);
