INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (378, 'Lead iOS Engineer at Boom Shakalaka (New York, NY)', '&lt;p&gt;"Will Steph Curry make his next shot?" "Will Eli Manning complete his next pass attempt?" Here at Boom Shakalaka we''re turning watching sports into an active, awesome experience.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you love technology, sports, and gaming then you''ll love what we''re doing. And we want to get to know you.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We: &lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Are passionate about building something great&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Are funded by top Silicon Valley and NYC investors who helped build Electronic Arts, Facebook, Zynga, and Playdom&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Were selected for Grand Central Tech''s year-long accelerator and Stanford''s StartX accelerator&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Are based in a beautiful office on Madison Ave where we''re surrounded by incredible people&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Offer competitive compensation packages, including a meaningful equity stake, benefits, and perks&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;You: &lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Are an exceptional iOS developer&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Have the confidence to build what will become the biggest sports gaming app in the world&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Will shape both the product and the company&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Dream big and deliver&amp;nbsp;&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Are a fun person to be around with&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;If you like moving fast, growing quickly, and building something that people love -- we want to get to know you.&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/97175/lead-ios-engineer-boom-shakalaka', 'Thu, 08 Oct 2015 17:02:01 Z', '2015-10-08T17:02:01Z', 378);
