INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (386, 'DevOps Leader at Myriad Group (Paris, France)', '&lt;p&gt;Myriad is looking to recruit a strong Hosted Solutions Leader to join our Sub Data Division and help deliver our services to top operators around the World, located mainly in the Emerging Market (Africa, LATAM and ASIA).&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Your main mission as a Hosted Solutions Leader is to:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Successfully lead an international implementation team within 3 Locations (Mexico, Paris, Chengdu).&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Drive complex deployments personally and/or with the rest of the team.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Deliver &amp;amp; maintain a service in the cloud (AWS) or within the Mobile Operator Data Centres.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Plan and define the implementation strategy for new services in the cloud with the Value Added Services team.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in the services deployment with the rest of the team.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with R&amp;amp;D and Product Management to adapt internal changes.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensure optimum usage of Operation Resources.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;We are looking for a natural hands-on leader with a strong technical expertise and an ability to lead and develop teams both in location and virtual.&lt;/p&gt;', 'Paris, France', 'http://careers.stackoverflow.com/jobs/99918/devops-leader-myriad-group', 'Thu, 08 Oct 2015 17:02:01 Z', '2015-10-08T17:02:01Z', 386);
