INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (390, 'Junior/Intermediate Analyst Programmer/SQL Ninja at Agility CIS (Houston, TX)', '&lt;p&gt;As an Analyst Programmer, you will be working in a small&amp;nbsp;team at our satellite office in Houston, Texas, supporting and implementing changes to our products to meet our US clients'' needs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;In addition to regular&amp;nbsp;maintenance and support tasks for the client, you will also be involved in all aspects of the SDLC including designing and writing specifications, coding and development, testing and writing release documentation. Our product runs on Microsoft SQL Server so chances are you''ll already be a SQL Prodigy/Coding Ninja with high ambitions.&lt;/p&gt;', 'Houston, TX', 'http://careers.stackoverflow.com/jobs/91377/junior-intermediate-analyst-programmer-sql-ninja-agility-cis', 'Thu, 08 Oct 2015 17:02:01 Z', '2015-10-08T17:02:01Z', 390);
