INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (418, 'Full Stack Developer - Javascript/PHP at KnowRoaming Ltd. (Toronto, ON, Canada)', '&lt;p&gt;KnowRoaming is a growing&amp;nbsp;technology company headquartered in midtown Toronto.&amp;nbsp;We are looking two&amp;nbsp;Full Stack Engineers to join our team and help develop our customer-facing portals and web-apps.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Your role:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work along side a team of engineers in developing and maintaining applications that support our Smart Sticker technology.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with marketing, brand management and UI/UX designers in planning the look and feel of our website, customer portals and web-apps.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Please submit your CV and any relevant personal-projects (Git, web-apps etc) to&amp;nbsp;jobs@knowroaming.com&lt;/p&gt;', 'Toronto, ON, Canada', 'http://careers.stackoverflow.com/jobs/72160/full-stack-developer-javascript-php-knowroaming-ltd', 'Thu, 08 Oct 2015 16:02:01 Z', '2015-10-08T16:02:01Z', 418);
