INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (444, 'Software Engineer In Data at Dataminr (New York, NY)', '&lt;p&gt;&lt;span&gt;At Dataminr, we are looking for dedicated software engineers to help us sort, analyze and deliver relevant information from terabytes of unstructured data in the social media space. We strive for strong abstractions that allow for fast iteration, easy integration and short development cycles, allowing us to constantly learn from our data and empowering our clients to apply these insights toward product enhancements.&lt;/span&gt;&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/16119/software-engineer-in-data-dataminr', 'Thu, 08 Oct 2015 16:02:01 Z', '2015-10-08T16:02:01Z', 444);
