INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (450, 'Senior Tools &amp; Automation Engineer at NetSuite (San Mateo, CA)', '&lt;p&gt;We are looking for a Senior Tools and Automation Engineer who has low tolerance for repetitive, manual work and has a passion for building state of the art automation and orchestrating tools. You will have a chance to work on infrastructure and system provisioning, applications deployment, log analytics, etc.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;span&gt;Responsibilities:&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Primarily responsible for designing/implementing/supporting operational tools that improve efficiencies through process automation, integrations, and metrics collection.&amp;nbsp; The tools built will provide Business Intelligence to the company, enhance departmental efficiencies, and promote higher ROI on client renewals and retention&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;span&gt;Travel: (optional)&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;1-25%&lt;/p&gt;', 'San Mateo, CA', 'http://careers.stackoverflow.com/jobs/82791/senior-tools-automation-engineer-netsuite', 'Thu, 08 Oct 2015 16:02:01 Z', '2015-10-08T16:02:01Z', 41);
