INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (465, 'Senior J2EE Software Engineer for a Fraud-Prevention System at PAYMINT AG (Frankfurt am Main, Germany)', '&lt;p&gt;Sie &amp;uuml;bernehmen eine f&amp;uuml;hrende Rolle bei der Entwicklung unseres moderenen Fraud-Detection Systems. Sie entscheiden &amp;uuml;ber&amp;nbsp;sinnvolle Technologien und &amp;uuml;ber die architektonischen Ans&amp;auml;tze. Die Umsetzung erfolgt in einem kleinen Scrum-Team.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wir sind ein junges und motiviertes Team in dem sich jeder mit Kreatitvit&amp;auml;t und Eigenverantwortung engagiert. Flexible Arbeitszeiten und eine gute Kaffeemaschine sorgen ebenfalls daf&amp;uuml;r, dass Sie sich bei uns wohlf&amp;uuml;hlen.&lt;/p&gt;', 'Frankfurt am Main, Germany', 'http://careers.stackoverflow.com/jobs/99901/senior-j2ee-software-engineer-for-a-fraud-paymint-ag', 'Thu, 08 Oct 2015 15:02:02 Z', '2015-10-08T15:02:02Z', 465);
