INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (469, 'Software Developer | Application Engineer at ART+COM AG (Berlin, Deutschland)', '&lt;p&gt;We take a holistic view on software development. This is why we expect you to participate in all project phases, starting from concept work with our customers, realization within our interdisciplinary teams, introduction to QA through defining the deployment process.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are a young team with high standards. You can expect agile/evolutionary methods, flat hierarchies and a very friendly and helpful atmosphere.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsabilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Working as part of a project team of 2-4 developers, you will:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;work together with our concept and UX team to design cool user interfaces&lt;/li&gt;&lt;br /&gt;&lt;li&gt;choose appropriate frontend technology&lt;/li&gt;&lt;br /&gt;&lt;li&gt;collaborate with backend team to identify and design necessary service APIs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;do front-end implementation to highest quality standards&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Deutschland', 'http://careers.stackoverflow.com/jobs/99900/software-developer-application-engineer-art-pluscom-ag', 'Thu, 08 Oct 2015 15:02:02 Z', '2015-10-08T15:02:02Z', 469);
