INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (471, 'Web Developer Magento at LimeSoda (Wien, Austria)', '&lt;p&gt;&lt;span&gt;Seems like our shops are exceeding our customers expectations! So w&lt;/span&gt;e hardly manage to service all existing customers and had to start a waiting list for new projects. To keep it short&amp;nbsp;we are optimizing our processes and&amp;nbsp;need more manpower in our team of certified Magento experts. This is why you are on our mind :-)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Things you will do:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Backend programming on big shops in Magento&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Implementation of custom designs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Building interfaces to external systems like ERP, CRM, newsletter or fulfilment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Development of extensions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Optimization and service of existing projects&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Why should you choose LimeSoda?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;We&amp;nbsp;encourage you acquiring personal certifications like "Magento Developer"&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Good work life balance&lt;/li&gt;&lt;br /&gt;&lt;li&gt;One of the best Magento teams in Austria: Our team members are speakers on MeetMagento-Events, organice the Austrian Magento-Meetup and are well connected to the international community&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Wien, Austria', 'http://careers.stackoverflow.com/jobs/99891/web-developer-magento-limesoda', 'Thu, 08 Oct 2015 15:02:02 Z', '2015-10-08T15:02:02Z', 471);
