INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (478, 'Distributed Systems Engineers - Kafka, Spark, Mesos, Storm, Hadoop, HDFS at Skyscanner (London, UK)', '&lt;p&gt;&lt;strong&gt;Could your expertise in Distributed Systems Engineering drive our Success?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Skyscanner is Europe&amp;rsquo;s leading provider of flight and travel search sites and mobile applications, and one of the most innovative and fastest growing travel companies globally. &amp;nbsp;Our teams are driving a transformation in online travel search and our goal is to deliver game-changing solutions enabling Skyscanner to become the world leading, most trusted online travel search provider.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Data is the very core of our business and Distributed System Engineers are crucial to ensuring we manage and utilise this to benefit our users while driving success for our business and partners.&lt;br&gt;Your expertise, initiative, problem solving and passion will ensure continual improvements in quality, eradication of problems by automation and best in class levels of service.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;As a Distributed Systems Engineer at Skyscanner you will be responsible for building, scaling and continually improving our Data Infrastructure. From Day 1 you can expect a real variety of challenging and complex projects &amp;ndash; data collection, storage, processing and analytics to name just a few!&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;You can offer us:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;A hunger to learn, a desire to meet challenges and develop your career&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A real passion for automation and Distributed Systems Engineering&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;We can offer you:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;An excellent career move &amp;ndash; complexity, challenge and real variety&lt;/li&gt;&lt;br /&gt;&lt;li&gt;The space to be trusted to do what you do best. And to make decisions that matter&lt;/li&gt;&lt;br /&gt;&lt;li&gt;The potential to truly impact on our success&lt;/li&gt;&lt;br /&gt;&lt;li&gt;The opportunity to help improve a service that millions of people see and use every day&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/99896/distributed-systems-engineers-kafka-spark-skyscanner', 'Thu, 08 Oct 2015 15:02:02 Z', '2015-10-08T15:02:02Z', 211);
